import React, { useState, useEffect, useReducer } from 'react';
// Material UI
import Header from './components/Header.jsx';
import JournalGrid from './components/JournalGrid.jsx';
import PastEntry from './components/PastEntry.jsx';
import './App.css';

import axios from 'axios';
require('dotenv').config();


const URL = "/api/entries";

export default function App() {
  const entriesReducer = (state, action) => {
    switch (action.type) {
      case 'set':
        return { ...state, entries: action.entries };
      case 'addEntry':
        return { ...state, entries: [...state.entries, action.newProp] };
      case 'deleteEntry':
        return {
          ...state,
          entries: state.entries.filter(propertyItem => {
            return propertyItem.value !== action.propToDelete;
          })
        };
      case 'areFetching': // True when fetching all entries
        return { ...state, areFetching: action.value };
      case 'isFetching': // True when fetching one property
        return { ...state, isFetching: action.value };
      default:
        return state;
    }
  };
  const [{ entries, areFetching, isFetching }, dispatch] = useReducer(entriesReducer, {
    entries: [],
    areFetching: false,
    isFetching: false
  });

  return (
    <div className="App">
      <header className="App-header">
        <Header message="Good Afternoon, Will"></Header>
      </header>
      <body>
        <JournalGrid />
      </body>
    </div>
  );
}
