import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import useMediaQuery from '@material-ui/core/useMediaQuery';
import InsertPhotoIcon from '@material-ui/icons/InsertPhoto';
import { useTheme } from '@material-ui/core/styles';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    addBtn: {
        background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
        border: 0,
        borderRadius: 3,
        color: 'white',
        padding: '0 30px',
        minWidth: 275,
        minHeight: 275
    },
    dialogFormBody: {
        display: 'flex',
    },
    dialogInsertImage: {
        minWidth: 275,
        textAlign: 'center',
    }
}));

export default function JournalEditor({ questions /* array of strings */ }) {
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const theme = useTheme();
    const fullScreen = useMediaQuery(theme.breakpoints.down('sm'));

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            {/* Button to add new journal entry */}
            <Button className={classes.addBtn} onClick={handleClickOpen}>
                Write new entry
            </Button>
            <Dialog
                open={open}
                onClose={handleClose}
                aria-labelledby="form-dialog-title"
                fullScreen={fullScreen}
                maxWidth="lg"
                scroll="paper"
                fullWidth
            >
                <DialogTitle id="form-dialog-title">New Journal Entry</DialogTitle>
                <DialogContent className={classes.dialogFormBody}>
                    <DialogContent className={classes.dialogInsertImage}>
                        <Button>
                            <InsertPhotoIcon fontSize="large" />
                            Insert Images
                        </Button>
                    </DialogContent>
                    <DialogContent>
                        {
                            questions.map((question, idx) => {
                                return (
                                    <DialogContent>
                                        <DialogContentText>
                                            {question}
                                        </DialogContentText>
                                        <TextField
                                            autoFocus={idx === 0}
                                            margin="dense"
                                            id={"ans" + idx}
                                            label="Answer"
                                            type="text"
                                            fullWidth
                                            variant="filled"
                                            multiline
                                            rowsMax="4"
                                        />
                                    </DialogContent>
                                );
                            })
                        }
                    </DialogContent>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <Button onClick={handleClose} color="primary">
                        Submit
                    </Button>
                </DialogActions>
            </Dialog>
        </div>
    );
}
