import React from 'react';
import PropTypes from 'prop-types';
import { Card, CardMedia, CardActionArea } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(theme => ({
    card: {
        minWidth: 275,
        minHeight: 275,
        maring: theme.spacing(2),
        boxShadow: props => ('0 3px 5px 2px ' + props.color)//'0 3px 5px 2px rgba(255, 105, 135, .3)',
    }
}));

const JournalCard = ({ pic, title, onClick, moodColor }) => {
    const classes = useStyles(moodColor);

    // handleClick() {
    //     onClick();
    // }

    return (
        <Card className={classes.card}>
            <CardActionArea>
                <CardMedia
                    component="img"
                    alt="Monday"
                    height="275"
                    src={pic}
                    title={title}
                />
            </CardActionArea>
        </Card >

    );

}

JournalCard.propTypes = {
    pic: PropTypes.string,
    title: PropTypes.string,
    onClick: PropTypes.func.isRequired,
    moodColor: PropTypes.object.isRequired
};

export default JournalCard;
